  const [task, setTask] = useState("");

  const [todoList, setTodoList] = useState<ITask[]>([]);

  function addTask() {
    const idRandom = (num: number) => Math.floor(Math.random() * num);

    console.log(idRandom(10));
  }

  return (
    <div className="App">
      <header>
        <h2>Lists</h2>

        <input
          type="text"
          autoComplete="off"
          placeholder="Escrever task..."
          name="task"
          className="input"
          value={task}
